    public static Block registerFurniture(RegisterEvent event, String blockName, Block block, boolean registerItem) {
        block = registerBlock(event, blockName, block, false);
        if (registerItem) {
            PaladinFurnitureModBlocksItems.BLOCKS.add(block);
        }
        return block;
    }

    public static Block registerFurniture(RegisterEvent event, String blockName, Block block, int count) {
        block = registerBlock(event, blockName, block, false);
        PaladinFurnitureModBlocksItems.BLOCKS.add(block);
        return block;
    }

    public static Block registerBlock(RegisterEvent event, String blockName, Block block, boolean registerItem) {
        event.register(ForgeRegistries.Keys.BLOCKS, new Identifier(PaladinFurnitureMod.MOD_ID, blockName), () -> block);
        if (registerItem) {
            PaladinFurnitureModBlocksItems.BLOCKS.add(block);
        }
        return block;
    }

    public static Item registerItem(RegisterEvent event, String itemName, Item item) {
        event.register(ForgeRegistries.Keys.ITEMS, new Identifier(PaladinFurnitureMod.MOD_ID, itemName), () -> item);
        return item;
    }

    public static Item registerBlockItem(RegisterEvent event, String itemName, Block block) {
        if (block.getDefaultState().getMaterial() == Material.WOOD || block.getDefaultState().getMaterial() == Material.WOOL) {
            return registerItem(event, itemName, new BlockItem(block, new Item.Settings().group(PaladinFurnitureMod.FURNITURE_GROUP)) {
                @Override
                public int getBurnTime(ItemStack itemStack, @Nullable RecipeType<?> recipeType) {
                    return 300;
                }

            });
        }
        return registerItem(event, itemName, new BlockItem(block, new Item.Settings().group(PaladinFurnitureMod.FURNITURE_GROUP)));
    }
